INSERT INTO `teachers` (`id`, `last_name`, `first_name`, `full_name`, `dia_chi_nha`, `dia_chi_nha_2`, `mon_hoc`, `rating`, `so_nguoi_danh_gia`, `avatar`, `trinh_do_giang_day`, `gioi_thieu`, `hoc_van`, `video`, `pham_vi_di_lai`, `hoc_phi_tai_nha`, `hoc_phi_tai_lop`, `lich_giang_day`, `citytag`, `districttag`, `nametag`, `duration`, `status`, `vi_tri`, `hoc_phi`) VALUES
(1, NULL, NULL, 'Đinh Mạnh Hà', 'Chùa Bộc, quận Đống Đa', NULL, 'guitar', 5, 3, 'hoc_guitar_voi_dinhmanhha.jpg', 'Cơ bản', 'Mình bắt đầu tiếp xúc với âm nhạc khoảng cuối năm 2014, mình đã tốt nghiệp Đại học Phương Đông chuyên ngành Kiến Trúc tuy nhiên âm nhạc đã trở thành một phần không thể thiếu, nên mình đã quyết tâm theo đuổi âm nhạc. Mình bắt đầu giảng dạy từ 2016. Thần tượng âm nhạc của mình là Frank Sinatra, Paul Gilbert. Dòng nhạc ưa thích và theo đuổi: Rock, Metal, Pop. Phương pháp giảng dạy của mình sẽ phù hợp với từng học sinh khác nhau, từ đó mình sẽ đưa ra giáo trình phù hợp nhất. Ngoài ra mình còn đang là thành viên của ban nhạc School of rock. Mình cũng chơi bass và đã tham gia biểu diễn tại các quán cafe và nhiều sự kiện lớn nhỏ khác nhau như Rock Fest FPT, biểu diễn tại Bar +84,...\n', 'Đang học hệ Trung Cấp Đại học văn hóa nghệ thuật quân đội', 'https://www.youtube.com/embed/wq_O0eTHqyk', 5, 120, NULL, '<table class=\"table table-bordered\">\n      <thead>\n       <tr>\n        <th></th>\n        <th>T2</th>\n        <th>T3</th>\n        <th>T4</th>\n        <th>T5</th>\n        <th>T6</th>\n        <th>T7</th>\n        <th>CN</th>\n       </tr>\n      </thead>\n      <tbody>\n       <tr>\n        <th>Sáng</th>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td></td>\n        <td></td>\n        <td class=\"busy\"></td>\n        <td></td>\n       </tr>\n       <tr>\n        <th>Chiều</th>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n       </tr>\n       <tr>\n        <th>Tối</th>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n       </tr>\n      </tbody>\n     </table>', 'hanoi', 'dongda', 'dinhmanhha', 60, 1, 9999, NULL),
(2, NULL, NULL, 'Hoàng Tất Đạt', 'Tam Trinh, quận Hoàng Mai', NULL, 'guitar', 5, 10, 'hoc_guitar_voi_tatdat.jpg', 'Cơ bản', 'Mình là Đạt. Nói về khoảng thời gian mình bắt đầu thực sự thích nhạc thì chắc là từ cấp 3. Mình chịu ảnh hưởng rất nhiều của ban nhạc Bức Tường. Vì vậy mình thi vào trường xây dựng. Và hiện tại cũng là chủ nhiệm clb Guitar Đại học xây dựng. Mình bắt đầu giảng dạy khoảng 2 năm trước, tức là khoảng năm 2 đại học. Dòng nhạc sở trường của mình có lẽ là Pop, Rock, Metal. Thần tượng thì chắc chắn là ban nhạc The wall rồi, và đặc biệt là anh Trần Tuấn Hùng. Mình rất thích những câu guitar và phong cách biểu diễn của anh. Ngoài ra, mình cũng nghe nhạc của My Chemical Romance, Slipknot,… Trong quá trình giảng dạy, mình sẽ cố gắng truyền đạt một cách dễ hiểu nhất để sau khi không có giáo viên học sinh có thể tự học được một cách dễ dàng. . Đến với lớp học của mình, học sinh chỉ cần chuẩn bị đàn và tâm trạng tốt. Mình ngoài công việc hiện tại là chủ nhiệm clb Guitar Xây dựng, mình còn là bassist của ban nhạc Silencer và cũng tham gia biểu diễn ở các quán café khá nhiều. Sở trường của mình là đệm hát. ', '', 'https://www.youtube.com/embed/NZ9GU6svtOI', 5, 150, NULL, '<table class=\"table table-bordered\">\n      <thead>\n       <tr>\n        <th></th>\n        <th>T2</th>\n        <th>T3</th>\n        <th>T4</th>\n        <th>T5</th>\n        <th>T6</th>\n        <th>T7</th>\n        <th>CN</th>\n       </tr>\n      </thead>\n      <tbody>\n       <tr>\n        <th>Sáng</th>\n        <td></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n       </tr>\n       <tr>\n        <th>Chiều</th>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n       </tr>\n       <tr>\n        <th>Tối</th>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td></td>\n        <td class=\"busy\"></td>\n        <td></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n       </tr>\n      </tbody>\n     </table>\n', 'hanoi', 'hoangmai', 'hoangtatdat', 60, 1, 8, NULL),
(3, NULL, NULL, 'Phan Thị Thu Phương', 'Thượng Thanh, quận Long Biên', NULL, 'guitar', 5, 4, 'hoc_guitar_voi_phanthithuphuong.jpg', 'Cơ bản', 'Chào các bạn học sinh của hocgiday.com. Mình là Phan Phương, bạn bè vẫn hay gọi là Phương Phan hay “cô giáo Phương Phan”. Mình tiếp xúc với âm nhạc từ khá sớm, đó là khoảng cấp 2, nhưng mình mới thực sự chơi đàn được 4 năm. Mình không học qua trường lớp nào, nhưng mình có theo học 1 năm tại S.O.R. Sau một khoảng thời gian cảm thấy tự tin với kiến thức, mình bắt đầu việc giảng dạy, đến nay đã khoảng 2 năm. Thần tượng âm nhạc của mình là Neil Zaza, MCR,… Mình yêu cầu với mỗi học sinh khá đơn giản là đàn và thái độ tự giác luyện tập. Thành tích của mình là 3 show của School Of Rock, 2 chương trình The Avengers, các chương trình nhỏ ở trường, câu lạc bộ và vài show nhỏ khác.', '', 'https://www.youtube.com/embed/A5icBJpMyzc', 3, 150, 100, '<table class=\"table table-bordered\">\n      <thead>\n       <tr>\n        <th></th>\n        <th>T2</th>\n        <th>T3</th>\n        <th>T4</th>\n        <th>T5</th>\n        <th>T6</th>\n        <th>T7</th>\n        <th>CN</th>\n       </tr>\n      </thead>\n      <tbody>\n       <tr>\n        <th>Sáng</th>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td class=\"busy\"></td>\n       </tr>\n       <tr>\n        <th>Chiều</th>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td class=\"busy\"></td>\n       </tr>\n       <tr>\n        <th>Tối</th>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td></td>\n        <td class=\"busy\"></td>\n       </tr>\n      </tbody>\n     </table>\n\n', 'hanoi', 'longbien', 'phanthithuphuong', 90, 1, 15, NULL),
(4, NULL, NULL, 'Nguyễn Hải Minh', 'Lò Đúc, quận Hai Bà Trưng', NULL, 'guitar', 5, 7, 'hoc_guitar_voi_nguyenhaiminh.jpg', 'Cơ bản, Nâng cao, Chuyên sâu', 'Mình là Minh, mọi người hay gọi là Minh Tốc. Mình Bắt đầu tiếp xúc với âm nhạc từ khá sớm, và tự tìm hiểu và học guitar đến nay đã gần 10 năm, rất tự tin về chuyên môn của bản thân, có thể dạy được đa dạng trình  độ từ cơ bản đến nâng cao và chuyên sâu. Mình vừa tốt nghiệp Cao đẳng nghệ thuật Hà Nội hệ trung cấp. Hiện đang học hệ đại học nhạc viện. Mình bắt đầu giảng dạy từ năm 2015. Dòng nhạc của mình là Jazz, Pop, Country, Blue, Rock, Metal,… Còn thần tượng thì có thể kể tới rất nhiều đó là Gurthie Govan, Dream Theater. Trong giờ học, mình muốn học sinh nghiêm túc và cần chuẩn bị đàn trước khi tới lớp. Nói về những hoạt động cá nhân, hiện mình thành viên ban nhạc Maybug và Sweepy, và mình đã có nhiều kinh nghiệm trong làm việc với VTV và công việc Studio, và việc sáng tác âm nhạc,…', 'Đã tốt nghiệp hệ Trung Cấp Cao đẳng nghệ thuật Hà Nội', 'https://www.youtube.com/embed/7b6QyJf324s', 5, 220, 180, '<table class=\"table table-bordered\">\n      <thead>\n       <tr>\n        <th></th>\n        <th>T2</th>\n        <th>T3</th>\n        <th>T4</th>\n        <th>T5</th>\n        <th>T6</th>\n        <th>T7</th>\n        <th>CN</th>\n       </tr>\n      </thead>\n      <tbody>\n       <tr>\n        <th>Sáng</th>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n       </tr>\n       <tr>\n        <th>Chiều</th>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n        <td class=\"busy\"></td>\n       </tr>\n       <tr>\n        <th>Tối</th>\n        <td></td>\n        <td></td>\n   